import 'package:floor/floor.dart';

@entity
class Sleep {
  //id will be the primary key of the table. Moreover, it will be autogenerated.
  //id is nullable since it is autogenerated.
  @PrimaryKey(autoGenerate: false)
  final DateTime dateTime;

  final DateTime startTime;

  final DateTime endTime;

  final int minAsleep;

  final int timeInBed;

  final int rem;

  final int deep;

  final int light;

  final int wake;

  //Default constructor
  Sleep(this.dateTime, this.startTime, this.endTime, this.minAsleep,
      this.timeInBed, this.rem, this.deep, this.light, this.wake);
} //Sleep